$row = 5;
//循环上半区层数从第0层到层数减一
for ($i=0; $i < $row; $i++) { 
	//先打印左侧的空格：总层数减去当前层数减1
	for ($j=0; $j < $row-$i-1; $j++) { 
		echo "&nbsp";
	}
	//打印“*”号，当前层数乘以2加1
	for ($k=0; $k < $i*2+1; $k++) { 
		echo "*";
	}
	echo "<br/>";
}
//循环下班去层数从上半区总层数减1
for ($i=0; $i < $row-1; $i++) { 
	//打印左侧空格等于下班去当前的层数
	for ($j=0; $j <= $i; $j++) { 
		echo "&nbsp";
	}
	//打印“*”号，下半区总层数减去当前层数乘以2加1
	for ($k=0; $k < ($row-1-$i)*2-1; $k++) { 
		echo "*";
	}
	echo "<br/>";
}

echo "<br/>方法：通过整个层数判定分区进行输出（必须为奇数层数）<br/>";
//定义所打印菱形的总层数
$row=9;
//定义上半区的总层数
$n = ceil($row/2);
//循环所有层数
for ($i=0; $i < $row; $i++) { 
