		//打印“*”号，当前层数乘以2减1
		for ($k=0; $k < $i*2+1; $k++) { 
			echo "*";
		}
		echo "<br/>";
	}else{
		//如果条件不满足上去，开始打印下半区信息
		//先打印空格，等于当前层数减去上半区层数
		for ($j=0; $j < $i-$n+1; $j++) { 
			echo "&nbsp";
		}
		//打印“*”号，等于总层数减去已经打印层数乘以2加1
		for ($k=0; $k < ($row-$i)*2-1; $k++) { 
			echo "*";
		}
		echo "<br/>";
	}
}

