	}
	echo "<br/>";
}

echo "<br/>方法：通过整个层数判定分区进行输出（必须为奇数层数）<br/>";
//定义所打印菱形的总层数
$row=9;
//定义上半区的总层数
$n = ceil($row/2);
//循环所有层数
for ($i=0; $i < $row; $i++) { 
	//判断若小于上半区层数进行上半区的循环
	if ($i<$n) {
		//打印空格上半区总层数减去当前层数
		for ($j=0; $j < $n-$i-1; $j++) { 
			echo "&nbsp";
		}
		//打印“*”号，当前层数乘以2减1
		for ($k=0; $k < $i*2+1; $k++) { 
			echo "*";
		}
		echo "<br/>";
	}else{
		//如果条件不满足上去，开始打印下半区信息
		//先打印空格，等于当前层数减去上半区层数
		for ($j=0; $j < $i-$n+1; $j++) { 
			echo "&nbsp";
		}
		//打印“*”号，等于总层数减去已经打印层数乘以2加1
		for ($k=0; $k < ($row-$i)*2-1; $k++) { 
