class Person{
	public static $name = '人类';
	public static function show(){
		// echo '这个星球的主宰者是：'.$this->name;(报错：Fatal error: Using $this when not in object context in)
		echo '这个星球的主宰者是：'.Person::$name;//(正确)
	}
}

class Student extends Person{

}
