<!DOCTYPE html>
<html lang="zh-cn">
<head>
	<meta charset="UTF-8">
	<style type="text/css">
		*{
			margin: 0px;
			padding: 0px;
		}
		#blood{
			margin-top:5px;
			width: 300px;
			height: 30px;
			border: 1px solid red;
			display:block;
		}
		#blood2{
			display: inline-block;
			width:300px;
			height: 30px;
			background: red;
		}
	</style>
	<script type="text/javascript">
	var time,time2,time3;
	var count=0;
	var score = 0;
	var i=1000;
	var n=0;
		//
		function start(){
			window.clearInterval(time);
			if(i>100){i-=100;}
			//10
			time = window.setInterval('show()',i);
		}
			//
			function speed(){
			window.clearInterval(time2);
			window.clearInterval(time3);
			//
			time3 = window.setInterval("timeOver()",1000);
			//
			window.setTimeout("start()",0);
			time2 = window.setInterval("start()",5000);
		}
		//
		function show(){
			//
			var obj = document.createElement('img');
			//
			//
			var size = Math.floor(Math.random()*30+30);
			//,
			var h = parseInt(screen.availHeight);
			var w = parseInt(screen.availWidth);
			var x = Math.floor(Math.random()*(w-w*0.2)+w*0.1);
			var y = Math.floor(Math.random()*(h-h*0.4)+h*0.1);
			//src
			obj.src="images/xingxing.png";
			obj.width = size;
			//topleft
			obj.style.position = "absolute";
			obj.style.top = y + "px";
			obj.style.left = x+ "px";
			//
			document.body.appendChild(obj);
			//
			count++;
			//
			var obj4 = document.getElementById("blood2");
			obj4.style.width = 300-(count * 30) +"px";
			//
			if (count>=10){
				alert(""+score+"");
				window.clearInterval(time);
				window.clearInterval(time2);
				window.clearInterval(time3);
				//
				location.reload();
			}
			//
			obj.onclick=remove;
		}
		//
		function remove(){
			this.parentNode.removeChild(this);
			//
			count--;
			//
			score++;
			var obj5=document.getElementById("score");
			obj5.innerHTML=":"+score+"";
		}
		//
		function timeOver(){
			n++;
			var obj3 = document.getElementById("overTime");
			obj3.innerHTML=""+n+"";
		}
		//
		function pause(){
			alert("");
		}
	</script>
	<title></title>
</head>
<body>
<input type="button" value="" onclick="speed()"></input>
<input type="button" value="" onclick="pause()"></input>
<span id="overTime">0</span>
<span id="score">:0</span>
<span id="blood"><span id="blood2"></span></span>
</body>
</html>
```

