<!DOCTYPE html>
<html lang="zh-cn">
<head>
	<meta charset="UTF-8">
	<title></title>
</head>
<body>
<?php 


	function bubbleSort($arr){
	//
	// 
	//N N-1
		for ($i=0,$len = count($arr); $i < $len-1; $i++) { 
			// 
			//// N-1
			// N-2
			for ($j=0; $j < $len-1-$i; $j++) { 
				if ($arr[$j]>$arr[$j+1]) {
					$temp = $arr[$j];
					$arr[$j]=$arr[$j+1];
					$arr[$j+1]=$temp;
				}
			}
		}
		return $arr;
	}

	function selectSort($arr){
		//
		//
		// 
		//$i  
		for ($i=0,$len=count($arr); $i < $len-1; $i++) { 
			$p=$i;
			for ($j=$i+1; $j < $len; $j++) { 
				if($arr[$j]<$arr[$p]){
					$p=$j;
				}
			}
			if ($p!=$i) {
				$temp = $arr[$i];
				$arr[$i]=$arr[$p];
				$arr[$p]=$temp;
			}

		}
		return $arr;
	}
	function insertSort($arr){
		//
		//
		for ($i=1,$len=count($arr); $i < $len; $i++) { 
			$temp=$arr[$i];
			for ($j=$i-1; $j >= 0; $j--) { 
				if ($temp<$arr[$j]) {
					$arr[$j+1]=$arr[$j];
					$arr[$j]=$temp;
				}else{
					break;
				}
			}
		}
		return $arr;
	}
/**
*
*
* @param array $arr 
*
* @return array 
*/
function quick_sort($arr) {
    //
    $length = count($arr);
    if($length <= 1) {
        return $arr;
    }
    // 1
    //
    //
    $base_num = $arr[0];
    // 
    //
    $left_array = array();//
    $right_array = array();//
    for($i=1; $i<$length; $i++) {
        if($base_num > $arr[$i]) {
            //
            $left_array[] = $arr[$i];
        } else {
            //
            $right_array[] = $arr[$i];
        }
    }
    //   
    //,
    $left_array = quick_sort($left_array);
    $right_array = quick_sort($right_array);
    //  
    return array_merge($left_array, array($base_num), $right_array);
}

	$myarr=array(23,45,64,21,5,75);
	print_r(bubbleSort($myarr));
	echo "<br/>";
	print_r(selectSort($myarr));
	echo "<br/>";
	print_r(insertSort($myarr));
	echo "<br/>";
	print_r(quick_sort($myarr));

 ?>
</body>
</html>
```

