<!DOCTYPE html>
<html lang="zh-cn">
<head>
	<meta charset="UTF-8">
	<title></title>
	<style type="text/css">
	*{
		margin: 0;
		padding: 0;
	}
	div {
		margin:50px;
		width: 470px;
	}
	div ul{
		float: right;
		margin-top:45px;
	}
	div ul li{
		list-style:none;
		width:30px;
		height: 20px;
		line-height: 20px;
		text-align: center;
		border: 1px solid orange;
		margin-top:5px;
	}
	</style>
	<script type="text/javascript">
		window.onload = init;		
		var i =1;
		var time;
		function init(){
			var obj = document.getElementById("d1");
			//
			obj.style.background = "orange";
			time = window.setInterval('change()',1000);
		}
		function change(){
			i++;
			if (i>5) {
				i=1;
			}
			var obj = document.getElementById('img');
			obj.src = "images/beijing_" + i + ".jpg";
			var obj2 = document.getElementById("d"+i);
			obj2.style.background = "orange";
			for (var j=1 ; j<6; j++){
				if(j!=i){
				var obj3 = document.getElementById("d"+j);
				obj3.style.background = "white";
				}
			}
		}
		function stop(){
			window.clearInterval(time);
		}
		function start(){
			time = window.setInterval('change()',1000);
