			$role = M('role')->find($info['admin_role_id']);
			$allow_auth_ac = $role['role_auth_ac'];//
			$now_auth_ac = CONTROLLER_NAME . '-' . ACTION_NAME; //
			if (FALSE === stripos($allow_auth_ac, $now_auth_ac)) {
				$this->error('');
