/**
 * Class UploadLib 
 */
class UploadLib{
    //
    private $error;
    //
    public function getError(){
        return $this->error;
    }
    /**
     * 
     * @param  array $file 
     * @return string       
     */
    public function upload($file){
        /**
         * 
         */
        if ($file['error']!==0) {
            switch ($file['error']) {
                case 1:
                    $this->error = "";
                    break;
                case 2:
                    $this->error = "";
                    break;
                case 3:
                    $this->error = "";
                    break;
                case 4:
                    $this->error = "";
                    break;
                case 6:
                    $this->error = "";
                    break;
                case 7:
                    $this->error = "";
                    break;
                default:
                    $this->error = "";
                    break;
            }
            return false;
        }
        /**
         * 
         * @param array $allow_types 
         * @param resource $finfo MIMEfinfo_file
         * @param string $info 
         * @param array $type_mime mime
         */
        $allow_types = array("image/jpeg","image/png","image/gif");
        $finfo = finfo_open(FILEINFO_MIME);
        $info = finfo_file($finfo, $file['tmp_name']);
        $info_array = explode(';',$info);
        $type_mime = $info_array[0];
        if (!in_array($type_mime,$allow_types)) {
            $this->error = "".$type_mime."";
            return false;
        }
        /**
         * 
         * @param byte $file['size'] 
         * @param byte $GLOBALS['config']['file']['allow_size'] 
         */
        if ($file['size']>$GLOBALS['config']['file']['allow_size']) {
            $this->error = ":".number_format($file['size']/1024/1024,2)."M;:".number_format($GLOBALS['config']['file']['allow_size']/1024/1024,2)."M";
            return false;
        }
        /**
         * HTTP POST
         */
        if (!is_uploaded_file($file['tmp_name'])) {
            $this->error = "HTTP";
            return false;
        }
        /**
         * 
         * @param array $type MIME
         * @param string $folder_set 
         * @param string $folder_path 
         * @param string $file_name 
         * @param string $file_path 
         */
        $type = explode('/', $type_mime);//
        $folder_set = $type[0].DS.$type[1].DS.date('Ym').DS;
        $folder_path = $GLOBALS['config']['file']['upload_path'].DS.$folder_set;
        $file_name = uniqid('',true).'.'.$type[1];
        $file_path = $folder_path.$file_name;
        if (!file_exists($folder_path)) {
            if (!mkdir($folder_path,0777,true)) {
                $this->error = "";
                return false;
            }

        }
        //
        if (move_uploaded_file($file['tmp_name'], $file_path)) {
            return $folder_set.$file_name;
        }else{
            $this->error = "";
        }
    }
}
?>
