/**
 * Class ThumbLib 
 */
class ThumbLib{
    //
    private $error;
    //
    public function getError(){
        return $this->error;
    }
    /**
     * 
     * @param string $src_path 
     * @param int $max_w 
     * @param int $max_h 
     * @param bool $is_ration 
     * @param string $prefix 
     * @return string 
     */
    public function thumb($src_path,$max_w='',$max_h='',$is_ratio=true,$prefix='small_'){
        $max_w = $max_w!='' ? $max_w : $GLOBALS['config']['file']['thumb_w'] ;
        $max_h = $max_h!=''	? $max_h : $GLOBALS['config']['file']['thumb_h'] ;
        $ext = strtolower(strrchr($src_path,'.'));//
        $type = '';
        switch ($ext) {
            case '.jpg':
                $type = 'jpeg';
                break;
            case '.jpeg':
                $type = 'jpeg';
                break;
            case '.png':
                $type = 'png';
                break;
            case '.gif':
                $type = 'gif';
                break;
            default:
                $this->error = "".$ext;
                return false;
        }
        $open_fn = 'imagecreatefrom'.$type; //
        // 
        $dst_img = imagecreatetruecolor($max_w, $max_h);
        // 
        $src_path_all = $GLOBALS['config']['file']['upload_path'].DS.$src_path;
        if(!$src_img = $open_fn($src_path_all)){
           $this->error = "";
            return false;
        }
        $src_w = imagesx($src_img);
        $src_h = imagesy($src_img);
        if ($is_ratio) {
            if ($src_w/$src_h>$max_w/$max_h) {
                #
                $dst_w = $max_w;
                $dst_h = $dst_w*$src_h/$src_w;
            }else{
                #
                $dst_h = $max_h;
                $dst_w = $dst_h*$src_w/$src_h;
            }
        }
        // 
        $dst_x = (int)(($max_w-$dst_w)/2);
        $dst_y = (int)(($max_h-$dst_h)/2);
        // 
        imagecopyresampled($dst_img, $src_img, $dst_x, $dst_y, 0, 0, $dst_w, $dst_h, $src_w, $src_h);
        // 
        $folderpath = dirname($src_path);//
        $filename = basename($src_path); //
        $small_path = $GLOBALS['config']['file']['upload_path'].DS.$folderpath.DS.$prefix.$filename; //
        $save_fn = 'image'.$type;
        $save_fn($dst_img,$small_path);
        // 
        imagedestroy($src_img);
        imagedestroy($dst_img);
        // 
        return $folderpath.DS.$prefix.$filename;
    }
}
?>
