laravel 
```php
public function register(){
    if($input = Input::all());
    // 
    $rules = [
        'pwd' => 'required|between:6,20',
        'name' => 'alpha_num',
        'name' => 'unique:people,tel',
        'email' => 'email',
        'tel' => 'integer',
    ];
    // 
    $message = [
        'name.alpha_num' => '',
        'name.unique' => '',
        'email.email' => '',
        'tel.integer' => '',
        'pwd.required' => '',
        'pwd.between'  => '6-20',
    ];
    $validator = Validator::make($input,$rules,$message);
    if($validator->passes()){//
        $pwd   = $input['pwd'];
        $tel   = $input['tel'];
        $email = $input['email'];
        // 
        $name = People::where('name','=',$input['name'])->first();
        $tel = People::where('tel','=',$input['tel'])->first();
        $email = People::where('email','=',$input['email'])->first();
        if($name!=null){
            $data = array(
            'sign' => 0,
            'code' => 'register error',
            'msg' => ''
             );
            return json_encode($data);
        }
        if($tel!=null){
            $data = array(
            'sign' => 0,
            'code' => 'register error',
            'msg' => ''
             );
            return json_encode($data);
        }
        if($email!=null){
            $data = array(
            'sign' => 0,
            'code' => 'register error',
            'msg' => ''
             );
            return json_encode($data);
        }
        $uid = DB::table('people_uid')->insertGetId(array());
        $input['uid']=$uid;
        $input['pwd'] = Crypt::encrypt($input['pwd']);
        DB::table('people')->insert($input);
        DB::table('people_index')->insert(array('uid'=>$uid,'name'=>$input['name']));
        DB::table('people_index')->insert(array('uid'=>$uid,'name'=>$input['tel']));
        $res = DB::table('people_index')->insert(array('uid'=>$uid,'name'=>$input['email']));
        if($res){
            // 
            $data = array(
                'sign' => 1,
                'code' => 'register success',
                'msg' => ''
            );
            echo json_encode($data);exit();
        }else{
            // 
            $data = array(
                'sign' => 0,
                'code' => 'insert error',
                'msg' => ''
            );
            echo json_encode($data);exit();
        }
    }else{
        // 
        $messages = $validator->messages();
        $data = array(
            'sign' => 0,
            'code' => 'insert error',
            'msg' => $messages->first(),
        );
        echo json_encode($data);exit();
    }
}
?>
```

